mapper.select:
SELECT
op.`id`,op.`owner_name`,ct.`pay_name`,op.`amount`,op.`createtime`,op.`finishtime`
FROM t_owner_paycat op,t_cost_type ct
  
	op.pay_item=ct.pay_item
	
		and STATUS=#{STATUS}
	
	
	and op.owner_name like '%' #{owner_name} '%'
	
	
		and op.p_m_id=#{pmid}
	 

ORDER BY op.createtime DESC
LIMIT
#{startCount},#{pageSize}

SELECT op.`id`,op.`owner_name`,ct.`pay_name`,op.`amount`,op.`createtime`,op.`finishtime` FROM t_owner_paycat op,t_cost_type ct
op.pay_item=ct.pay_item
and STATUS=#{STATUS}
and op.owner_name like '%' #{owner_name} '%'
and p_m_id=#{pmid}
and p_m_id in (select id from t_property_management_department where pid=#{pid})
ORDER BY op.createtime DESC LIMIT #{startCount},#{pageSize}

SELECT COUNT(*) FROM t_owner_paycat op,t_cost_type ct
op.pay_item=ct.pay_item
and STATUS=#{STATUS}
and op.owner_name like '%' #{owner_name} '%'
and op.p_m_id=#{pmid}

SELECT COUNT(*) FROM t_owner_paycat op,t_cost_type ct
op.pay_item=ct.pay_item
and STATUS=#{STATUS}
and op.owner_name like '%' #{owner_name} '%'
and p_m_id=#{pmid}
and p_m_id in (select id from t_property_management_department where pid=#{pid})

